              });
              res.push(ele.sections.length>1?`${item}-(${str})`:`${item}-${str}`)
            }
          });
        });
        console.log(res,"asasa");
        let str = res.join(', ')
           console.log(str,"asdhgyjasg");
      }, []);
    return (
        <Modal onRequestClose={()=>setshowIcecream(false)}>
            <Text>Ice Cream</Text>
            <Text>{route.params.name}</Text>
        </Modal>
    )
}

export default IceCream

const styles = StyleSheet.create({})
