import axios from "axios";
import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  TextInput,
  View,
  SafeAreaView,
  StatusBar,
} from "react-native";
import { Icon } from "react-native-elements";
import { Button } from "react-native-paper";
import { SafeAreaProvider } from "react-native-safe-area-context";

const OtpVerification = ({ navigation, route }) => {

  axios.get('')  
  const [OTP, setOTP] = useState([]);
  console.log(OTP);
  return (
    <View>
      <SafeAreaProvider>
        <SafeAreaView>
          <StatusBar />
        </SafeAreaView>
      </SafeAreaProvider>
      <View
        style={{
          backgroundColor: "white",
          height: "100%",
        }}
      >
        <View
          style={{
            flexDirection: "row",
            justifyContent: "flex-start",
            marginTop: 5,
          }}
        >
          <Icon type="antdesign" size={25} name="arrowleft" color="black" onPress={()=>{
              navigation.goBack()
          }} />
          <Text
            style={{
              fontSize: 20,
              letterSpacing: 2,
              marginLeft: 10,
            }}
          >
            OTP Verification
          </Text>
        </View>
        <Text
          style={{
            marginTop: 40,
            textAlign: "center",
            fontSize: 15,
          }}
        >
          We have sent a verifation code to
        </Text>
        <Text
          style={{
            marginTop: 5,
            textAlign: "center",
            fontSize: 20,
            marginBottom: 50,
          }}
        >{`+91-${route.params.phoneNumber}`}</Text>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            marginHorizontal: 20,
          }}
        >
          <TextInput
            keyboardType="number-pad"
            style={styles.inputBox}
            maxLength={1}
            onChangeText={(val) => {
              setOTP((prev) => {
                  console.log(val);
                prev[0] = val;
                return prev;
              });
            }}
          />
          <TextInput
            keyboardType="number-pad"
            maxLength={1}
            onChangeText={(val) => {
              //     let otp = OTP
              //    otp[1]=val
              setOTP((prev) => {
                prev[1] = val;
                return prev;
              });
            }}
            style={styles.inputBox}
          />
          <TextInput
            keyboardType="number-pad"
            maxLength={1}
            style={styles.inputBox}
            onChangeText={(val) => {
              setOTP((prev) => {
                prev[2] = val;
                return prev;
              });
            }}
          />
          <TextInput
            keyboardType="number-pad"
            maxLength={1}
            onChangeText={(val) => {
              setOTP((prev) => {
                prev[3] = val;
                return prev;
              });
            }}
            style={styles.inputBox}
          />
          <TextInput
            keyboardType="number-pad"
            maxLength={1}
            onChangeText={(val) => {
              setOTP((prev) => {
                prev[4] = val;
                return prev;
              });
            }}
            style={styles.inputBox}
          />
          <TextInput
            keyboardType="number-pad"
            maxLength={1}
            onChangeText={(val) => {
              setOTP((prev) => {
                prev[5] = val;
                return prev;
              });
            }}
            style={styles.inputBox}
          />
        </View>
        <View>
          <Button
          onPress={()=>{
              console.log(OTP.join(''));
          }}
            mode="contained"
            labelStyle={{
            //   color: "tomato",
            }}
            style={{
              marginHorizontal: 20,
              marginVertical: 50,
              paddingVertical: 10,
            //   borderColor: "tomato",
            //   backgroundColor:'tomato',
              borderWidth: 1,
            }}
          >
            Resend SMS
          </Button>
          <Button
          onPress={()=>{
              console.log(OTP.join(''));
          }}
            mode="contained"
            labelStyle={{
            //   color: "tomato",
            }}
            style={{
              marginHorizontal: 20,
            //   marginVertical: 50,
              paddingVertical: 10,
            //   borderColor: "",
              backgroundColor:'dodgerblue',
              borderWidth: 1,
            }}
          >
            Continue
          </Button>
        </View>
      </View>
    </View>
  );
};

export default OtpVerification;

const styles = StyleSheet.create({
  inputBox: {
    height: 50,
    textAlign:'center',
    color:'grey',
    width: 45,
    borderColor: "grey",
    borderRadius: 10,
    borderWidth: 0.5,
  },
});
